import React, { useState } from "react";
import styles from "./MainContainer.module.css";
import Card from "./Card";
import Arrow from "../svg/Arrow";
import ArrowLeft from "../svg/ArrowLeft";

function MainContainer() {
  const [textoCard, setTextoCard] = useState("Texto inicial del Card");
  const opcionesTexto = [
    "Primer cambio de texto",
    "Segundo cambio de texto",
    "Tercer cambio de texto"
  ];
  const [indiceTexto, setIndiceTexto] = useState(0);

  const cambiarTextoSiguiente = () => {
    const nuevoIndice = (indiceTexto + 1) % opcionesTexto.length;
    setIndiceTexto(nuevoIndice);
    setTextoCard(opcionesTexto[nuevoIndice]);
  };

  const cambiarTextoAnterior = () => {
    const nuevoIndice = (indiceTexto - 1 + opcionesTexto.length) % opcionesTexto.length;
    setIndiceTexto(nuevoIndice);
    setTextoCard(opcionesTexto[nuevoIndice]);
  };

  return (
    <div className={styles["main-container"]}>
      <div className={styles["main"]}>
        <button className={styles["arrow-button"]} onClick={cambiarTextoAnterior}>
          <ArrowLeft />
        </button>
        <Card texto={textoCard} />
        <button className={styles["arrow-button"]} onClick={cambiarTextoSiguiente}>
          <Arrow />
        </button>
      </div>
    </div>
  );
}

export default MainContainer;
