import React, { useState } from 'react'
import Footer from './footer/Footer'
import Header from './headerContainer/Header'
import SideBar from './sidebarContainer/SideBar'
import SubHeaderContainer from './subHeaderContainer/SubHeaderContainer'
import CardContainer from './cardContainer/CardContainer'
import ModalContainer from './modalContainer/ModalContainer'
import styles from './MainContainer.module.css'

function MainContainer() {
  const [isColumn, setIsColumn] = useState(false)

  function changeToColumnDisplay() {
    setIsColumn(!isColumn)
  }
  const [modalOpen, setModalOpen] = useState(false)
  const [info, setInfo] = useState([])
  const [details, setDetails] = useState({})
  const [number, setNumber] = useState('')
  function handleClick(info, cardDetails, number) {
    setNumber(number)
    setInfo(info)
    setDetails(cardDetails)
    modalOpen ? setModalOpen(false) : setModalOpen(true)
  }
  return (
    <>
      <Header />
      <div className={styles['main-border-body']}>
        <SideBar />
        <div className={styles['main-body']}>
          <SubHeaderContainer
            isColumn={isColumn}
            changeToColumnDisplay={changeToColumnDisplay}
          />
          <CardContainer isColumn={isColumn} handleClick={handleClick} />
          {modalOpen ? (
            <ModalContainer
              info={info}
              details={details}
              number={number}
              handleClick={handleClick}
            />
          ) : (
            <></>
          )}
        </div>
      </div>
    </>
  )
}

export default MainContainer
