import React, { useState } from 'react';
import styles from './Form.module.css';

const peliculas = [
  'Jhon Wick 4',
  'Jhon Wick 4',
  'Jhon Wick 4',
  'Jhon Wick 4',
  'Jhon Wick 4',
  'Jhon Wick 4',
  'Jhon Wick 4',
  'Jhon Wick 4',
  'Jhon Wick 4',
  'Jhon Wick 4'
];

function Form() {
  const [peliculaSeleccionada, setPeliculaSeleccionada] = useState('');
  const [nombreCompleto, setNombreCompleto] = useState('');
  const [email, setEmail] = useState('');
  const [resena, setResena] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Pelicula:', peliculaSeleccionada);
    console.log('Nombre:', nombreCompleto);
    console.log('Email:', email);
    console.log('Reseña:', resena);
  };

  return (
    <div className={styles['form-container']}>
      <form onSubmit={handleSubmit}>
        <div className={styles['form-cells']} >
          <label className={styles['input-label']}>Seleccione película</label>
          <select className={styles['input-field']} value={peliculaSeleccionada} onChange={(e) => setPeliculaSeleccionada(e.target.value)}>
            <option value="">Seleccione una película</option>
            {peliculas.map((pelicula, index) => (
              <option key={index} value={pelicula}>
                {pelicula}
              </option>
            ))}
          </select>

        </div>
        <div className={styles['form-cells']}>
  <label className={styles['input-label']}>Nombre completo</label>
  <input
    className={`${styles['input-field']} ${nombreCompleto.length < 4 ? styles['input-invalid'] : ''}`}
    type="text"
    value={nombreCompleto}
    onChange={(e) => setNombreCompleto(e.target.value)}
    minLength={4}
  />
  {nombreCompleto.length < 4 && (
    <p className={styles['input-invalid-message']}>El nombre debe tener al menos 4 caracteres</p>
  )}
</div>

        {/* ...otros campos y botones aquí... */}
      </form>
    </div>
  );
}

export default Form;
